Instruction.String:
ConflictDetection.isIdle = false;
if (!ConflictDetection.isIdleInternal)
	return;
ConflictDetection.isIdleInternal = false;

while (ConflictDetection.InQueue.Count > 0)
{	
	StatusArtifact sa = ConflictDetection.InQueue.Dequeue();	

	while ((sa.UpdateList == null || sa.UpdateList.Count == 0)&& ConflictDetection.InQueue.Count > 0)
	{
		sa = ConflictDetection.InQueue.Dequeue();
	}
	Simulation.MonSysContext.StatusArtifactContext.Update up = sa.UpdateList[0];	
	// each change results in the SA to be placed in the ChangedCode queue, 
	// so we only observe the first UPDATE list item, and remove it after processing.
	if (up.UpdateOnVersion == sa.Version && sa.Version == 0) 
	{			
		// new Code
		sa.Version++;
		up.isUpdateSuccess = true;
	}	
	else
	{	// evaluate update extend/scope	
		if (up.UpdateOnVersion != sa.Version && Random.Uniform() <= up.Scope) 
		{			

			sa.NrConflicts++;
			up.isUpdateSuccess = false;
			ConflictDetection.AccessCoordinator.UpdateConflict++;
		}
		else
		{
			up.isUpdateSuccess = true;
			sa.Version++;
			ConflictDetection.AccessCoordinator.UpdateSuccess++;
			// set the Link to the Update object that contains the right update, 
			// other updates afterwards are bound to fail
			// set the link in any case, as both success and failure cases need it
		}					
	}
	sa.UpdateList.RemoveAt(0);
	ConflictDetection.OutQueue.Enqueue(up);
}
ConflictDetection.isIdleInternal = true;

while (ConflictDetection.OutQueue.Count > 0)
{
	Simulation.MonSysContext.StatusArtifactContext.Update up = ConflictDetection.OutQueue.Dequeue();
	//up.WorkingOnUpdate.ReadFeedback(false); not working because LINK is null, not sure why, should not be.
	up.Responsible.ReadFeedback(false);
}
ConflictDetection.isIdle = true;

MonSys ms = Calculator.MetricCalculator.MonSys;

// calculate overall Agent Load here as this is one of the most regularly executed methods:
int agentCount = ms.AgentList.Count;
double agentEffSum = 0;
double agentDurSum = 0;
double agentUpdateSum = 0;

foreach (Agent agent in ms.AgentList)
{
	agentDurSum += Math.Max(agent.LastUpdateDur, agent.ContributorList[0].Dur);
	//agentDurSum += agent.LastUpdateDur;
	agentEffSum += Math.Max(agent.LastUpdateEff, agent.ContributorList[0].Effort);
	//agentEffSum += agent.LastUpdateEff;		
	agentUpdateSum += agent.UpdateCount;
}

ms.AgentUpdateEff = agentEffSum/agentCount;
ms.AgentUpdateDur = agentDurSum/agentCount; 
ms.UpdatesPerTime = agentUpdateSum / Model.Simulation.Simulator.Scheduler.Time;

// determine where to get version Nr from
// determine update extend/scope
// determine update duration
StatusArtifact sa = Contributor.SharedStatusArtifact;

Simulation.MonSysContext.StatusArtifactContext.Update up = Contributor.WorkingOnUpdate;
if (up == null)
{
	// working on new update
	Contributor.WriteTries = 1;
	Contributor.Agent.UpdateCount++;
}
else
{
	Contributor.WriteTries++;
	// working on conflicting update
	Contributor.Agent.UpdateConflictSum++;		
}
up = sa.Create("Update") as Simulation.MonSysContext.StatusArtifactContext.Update;
Contributor.WorkingOnUpdate = up;
up.WorkingOnUpdate = Contributor;
up.Responsible = Contributor;
up.Scope = Contributor.Extend;
up.UpdateOnVersion = Contributor.ArtifactVersion;
Contributor.Dur = Model.Simulation.Simulator.Scheduler.Time-Contributor.StartTime;

//double dur = 1+Contributor.Extend;
double dur =1;
Contributor.Effort += dur;
return dur;

if (Contributor.WorkingOnUpdate != null && Contributor.WorkingOnUpdate.isUpdateSuccess)
{
	Contributor.ArtifactVersion = Contributor.WorkingOnUpdate.StatusArtifact.Version;
	Contributor.Agent.UpdateConflictPerWrite = (Contributor.WriteTries-1);
	Contributor.Dur = Model.Simulation.Simulator.Scheduler.Time -Contributor.StartTime;

	Contributor.Agent.EWMAUpdateDur = Contributor.Agent.EWMAalpha*Contributor.Dur + (1-Contributor.Agent.EWMAalpha)*Contributor.Agent.EWMAUpdateDur;
	Contributor.Agent.EWMAUpdateEff = Contributor.Agent.EWMAalpha*Contributor.Effort + (1-Contributor.Agent.EWMAalpha)*Contributor.Agent.EWMAUpdateEff;
	Contributor.Agent.LastUpdateDur = Contributor.Dur;
	Contributor.Agent.TotalUpdateDur += Contributor.Dur;
	Contributor.Agent.LastUpdateEff = Contributor.Effort;
	Contributor.Agent.TotalUpdateEff += Contributor.Effort;


	if (Contributor.DoArtifactLocking)
	{
		Contributor.Agent.UpdateConflictPerWrite = (Contributor.LockTries-1);
	}
}

ReadFeedback.Contributor.ArtifactVersion = ReadFeedback.Contributor.SharedStatusArtifact.Version;

if (ReadFeedback.Contributor.DoArtifactLocking)
{
	DomainProAnalyst.Instance.Report("Conflict even though artifact locking applied!");
}

if (Contributor.SharedStatusArtifact.LockedBy == null)
{
	Contributor.SharedStatusArtifact.LockedBy = Contributor;
	Contributor.hasLock = true;
}
else
{
	Contributor.WriteTries++;
}
Contributor.Dur = Model.Simulation.Simulator.Scheduler.Time-Contributor.StartTime;

if (Contributor.SharedStatusArtifact != null && 
	Contributor.SharedStatusArtifact.LockedBy == Contributor)
{
	Contributor.SharedStatusArtifact.LockedBy = null;
}
Contributor.hasLock = false;

if (Contributor.SharedStatusArtifact == null)
{
	Choice= -1;
	return;
}

Contributor.Dur = 0;
Contributor.Effort = 0;
Contributor.LockTries = 0;

Contributor.Agent.UpdatePerTime = Contributor.Agent.UpdateCount /  Model.Simulation.Simulator.Scheduler.Time;


double rand = Random.Uniform();
double updateProb = Contributor.UpdateP;
if (Contributor.Agent.IsHotspot)
	updateProb = updateProb*2;

if (Contributor.ExtentQueue.Count > 0)  
{
	Choice = 2;
	Contributor.Extend = Contributor.ExtentQueue.Dequeue();
}
else
{
	if (rand <= updateProb)
	{
		Choice = 2;
		Contributor.Extend = Contributor.CreateP;
	}
	else 
		Choice = 0;
}

if (Contributor.Agent.IsHotspot)
	return Contributor.Agent.WorkExeDuration / 2;
else
	return Contributor.Agent.WorkExeDuration;

SharedStatusArtifactLink = Agent.MonSys.Create("SharedStatusArtifact") as SharedStatusArtifact;
WorkingOnUpdateLink = Agent.MonSys.Create("WorkingOnUpdate") as WorkingOnUpdate;

if (IdleP <= 0)
	DoArtifactLocking = true;
else
	DoArtifactLocking = false;


SharedStatusArtifact = Agent.MonSys.StatusArtifactList[0];

HotSpot.CurrHotSpotId++;
HotSpot.Undetected = HotSpot.UndetectedDict.Count;
double timeNow = Model.Simulation.Simulator.Scheduler.Time;


// reset all sensors
foreach(Agent boa in HotSpot.MonSys.AgentList)
{
	boa.IsHotspot = false;
}
int sourceCount = HotSpot.MonSys.AgentList.Count;
int sel = Math.Min(HotSpot.CurrHotSpotId * (int)(sourceCount/10), sourceCount);

if (HotSpot.IsBurstPhase)
{
	HotSpot.IsBurstPhase = false;
}
else
{		
	HotSpot.IsBurstPhase = true;
	String hsUn = "";
	foreach (int hsid in HotSpot.UndetectedDict.Keys)
	{
		hsUn = hsUn+", "+hsid;
	}
	//DomainProAnalyst.Instance.Report("Undetected Situations: "+hsUn+" @:"+timeNow);
	HotSpot.UndetectedDict.Add(HotSpot.CurrHotSpotId,timeNow);
	sel = 0;
	HotSpot.CurrHotSpotId--;
}

DomainProAnalyst.Instance.Report("Hotspot Switching Situation "+HotSpot.CurrHotSpotId+" with burst sources: "+sel +" @:"+timeNow);
// select sources
HotSpot.EventBurstSourceCount = sel;

while (sel > 0 && sourceCount > 0) {
   if( HotSpot.RandomList[(HotSpot.RandomCounter++)%HotSpot.RandomList.Count] < sel / sourceCount ) {
		// set to burst mode
		HotSpot.MonSys.AgentList[sourceCount-1].IsHotspot = true;
     		sel--;
	}
	sourceCount--;
}

MonSys ms = HotSpot.MonSys;

// calculate overall Agent Load here as this is one of the most regularly executed methods:
int agentCount = ms.AgentList.Count;
double agentEffSum = 0;
double agentDurSum = 0;
double agentUpdateSum = 0;
foreach (Agent agent in ms.AgentList)
{	
	agentDurSum += agent.TotalUpdateDur;	
	agentEffSum += agent.TotalUpdateEff;		
	agentUpdateSum += agent.UpdateCount;
}
double updateDiff = agentUpdateSum - HotSpot.PhaseUpdates;

ms.AgentUpdateEffPhase = (agentEffSum-HotSpot.PhaseEffort)/updateDiff;
ms.AgentUpdateDurPhase = (agentDurSum-HotSpot.PhaseDuration)/updateDiff; 
ms.UpdatesPerPhase = (agentUpdateSum-HotSpot.PhaseUpdates)/(Model.Simulation.Simulator.Scheduler.Time-HotSpot.PhaseTime);
ms.StatusArtifactList[0].ConflictPerUpdatePhase = (ms.StatusArtifactList[0].NrConflicts - HotSpot.PhaseConflicts)/(agentUpdateSum - HotSpot.PhaseUpdates);

HotSpot.PhaseEffort = agentEffSum;
HotSpot.PhaseDuration = agentDurSum;
HotSpot.PhaseUpdates = agentUpdateSum;
HotSpot.PhaseConflicts = ms.StatusArtifactList[0].NrConflicts;
HotSpot.PhaseTime = Model.Simulation.Simulator.Scheduler.Time;

CurrHotSpotId = 0;
double timeNow = Model.Simulation.Simulator.Scheduler.Time;
UndetectedDict.Add(CurrHotSpotId,timeNow);

for (int i = 0; i < 10000; i++)
{
	RandomList.Add(Random.Uniform());
}

if (SeedU > 0 && SeedV > 0)
{
	Random.Seed(SeedU, SeedV);
