newT = Master.BackOfficeAgent.CrowdPattern.Create("AnalysisTask") as AnalysisTask;
newT.owner = Master;
newT.createdOn = Model.Simulation.Simulator.Scheduler.Time;

newT.reqSkills.Add("ImageJob");

