Master.TaskCount++;

double rand = Random.Uniform();
double prob =  Master.CreateP;
if (Master.BackOfficeAgent.IsHotspot)
{
	prob = prob*2;	
}
if (rand <= prob)
	choice = 1;
else 
	choice = 0;

if (Task.reqSkills.Contains("ImageJob"))
{
	if (TaskAssignment.IdleWorkers.Count > 0)
	{
		CrowdPatternContext.ExpertContext.Worker worker = TaskAssignment.IdleWorkers.Dequeue();
		worker.Task = Task;
		worker.ProcessJob(false);
	}
	else
	{
		TaskAssignment.JobList.Add(Task);
		TaskAssignment.AnalysisTeam.OpenJobCount++;
	}
	TaskAssignment.AnalysisTeam.IdleWorkerCount = TaskAssignment.IdleWorkers.Count;
}
// Queuing Strategy Check:
if(TaskAssignment.IdleP == 0)
{
	if (TaskAssignment.AnalysisTeam.EWMATaskDur > (TaskAssignment.AnalysisTeam.CrowdPattern.ExpertList[0].WorkExeDuration *2))
	{
		TaskAssignment.AnalysisTeam.JobStrategy = "LIFO";
		TaskAssignment.AnalysisTeam.JobStrategyChange = 1; 
	}
	if (TaskAssignment.JobList.Count < TaskAssignment.AnalysisTeam.CrowdPattern.ExpertList.Count )
	{
		TaskAssignment.AnalysisTeam.JobStrategy = "FIFO";
		TaskAssignment.AnalysisTeam.JobStrategyChange = 0;
	}
}

if(IdleP > 0)
	AnalysisTeam.JobStrategy = "FIFO";
else if(IdleP < 0)
	AnalysisTeam.JobStrategy = "LIFO";
else
	AnalysisTeam.JobStrategy = "FIFO";

AnalysisResult ar = CrowdPattern.Create("AnalysisResult") as AnalysisResult;
TaskRefConnLink = CrowdPattern.Create("TaskRefConn") as TaskRefConn;
TaskRefConn = ar;

AnalysisResult.createdOn = Model.Simulation.Simulator.Scheduler.Time;
AnalysisResult.duration = AnalysisResult.createdOn - AnalysisResult.TaskRefConn.createdOn;

double alpha = AnalysisResult.CrowdPattern.AnalysisTeamList[0].EWMAalpha;
AnalysisResult.CrowdPattern.AnalysisTeamList[0].LastTaskDur = AnalysisResult.duration;
AnalysisResult.CrowdPattern.AnalysisTeamList[0].TotalTaskDur += AnalysisResult.duration;
AnalysisResult.CrowdPattern.AnalysisTeamList[0].TaskCount++;
AnalysisResult.CrowdPattern.AnalysisTeamList[0].EWMATaskDur = alpha*AnalysisResult.duration + (1-alpha)*AnalysisResult.CrowdPattern.AnalysisTeamList[0].EWMATaskDur;
AnalysisResult.CrowdPattern.AnalysisTeamList[0].AvgTaskDur = AnalysisResult.CrowdPattern.AnalysisTeamList[0].TotalTaskDur/AnalysisResult.CrowdPattern.AnalysisTeamList[0].TaskCount;

Worker.Task = null;
CrowdPatternContext.AnalysisTeamContext.TaskAssignment ta = Worker.Expert.CrowdPattern.AnalysisTeamList[0].TaskAssignmentList[0];
if (ta.JobList.Count > 0)
{
	if (ta.AnalysisTeam.JobStrategy == "FIFO")
	{	

		Worker.Task = ta.JobList[0];
		ta.JobList.RemoveAt(0);
		ta.AnalysisTeam.OpenJobCount--;
	}
	else
	{
		int last = ta.JobList.Count;
		Worker.Task = ta.JobList[last-1];
		ta.JobList.RemoveAt(last-1);
		ta.AnalysisTeam.OpenJobCount--;
	}
	if (Worker.Task == null)
	{
		ta.IdleWorkers.Enqueue(Worker);
	}
	else
		Worker.IsIdle = false;
}
else
{
	ta.IdleWorkers.Enqueue(Worker);
}

HotSpot.CurrHotSpotId++;
HotSpot.Undetected = HotSpot.UndetectedDict.Count;
double timeNow = Model.Simulation.Simulator.Scheduler.Time;


// reset all sensors
foreach(BackOfficeAgent boa in HotSpot.CrowdPattern.BackOfficeAgentList)
{
	boa.IsHotspot = false;
}
int sourceCount = HotSpot.CrowdPattern.BackOfficeAgentList.Count;
int sel = Math.Min(HotSpot.CurrHotSpotId * (int)(sourceCount/10), sourceCount);

if (HotSpot.IsBurstPhase)
{
	HotSpot.IsBurstPhase = false;
}
else
{		
	HotSpot.IsBurstPhase = true;
	String hsUn = "";
	foreach (int hsid in HotSpot.UndetectedDict.Keys)
	{
		hsUn = hsUn+", "+hsid;
	}
	//DomainProAnalyst.Instance.Report("Undetected Situations: "+hsUn+" @:"+timeNow);
	HotSpot.UndetectedDict.Add(HotSpot.CurrHotSpotId,timeNow);
	sel = 0;
	HotSpot.CurrHotSpotId--;
}

DomainProAnalyst.Instance.Report("Hotspot Switching Situation "+HotSpot.CurrHotSpotId+" with burst sources: "+sel +" @:"+timeNow);
// select sources
HotSpot.EventBurstSourceCount = sel;

while (sel > 0 && sourceCount > 0) {
   if( HotSpot.RandomList[(HotSpot.RandomCounter++)%HotSpot.RandomList.Count] < sel / sourceCount ) {
		// set to burst mode
		HotSpot.CrowdPattern.BackOfficeAgentList[sourceCount-1].IsHotspot = true;
     		sel--;
	}
	sourceCount--;
}

CurrHotSpotId = 0;
double timeNow = Model.Simulation.Simulator.Scheduler.Time;
UndetectedDict.Add(CurrHotSpotId,timeNow);

for (int i = 0; i < 10000; i++)
{
	RandomList.Add(Random.Uniform());
}

CrowdPatternList[0].ScheduledTasks = 0;
CrowdPatternList[0].MaxTasks = CrowdPatternList[0].BackOfficeAgentList.Count*300;
DomainProAnalyst.Instance.Report("Simulation Initialize: will stop producing tasks at task Nr: "+CrowdPatternList[0].MaxTasks);
